mapper.select:
SELECT NO,NAME,PRIOR_NO priorNo,REGDATE
FROM categories
WHERE no=#{no}

SELECT eng_name engName FROM categories
WHERE no=#{no}
WHERE no = (SELECT prior_no FROM categories WHERE no = #{no})

SELECT no,name,prior_no priorNo, regdate
FROM categories
WHERE prior_no = #{priorNo}

SELECT no,name,prior_no priorNo, regdate
FROM categories
WHERE no = #{priorNo}

SELECT no, name, prior_no priorNo, regdate
FROM categories 
WHERE prior_no=#{no}

SELECT name
FROM categories
WHERE no = #{no}
