mapper.select:
SELECT NO,GIVER_NO giverNo,CATEGORY_NO categoryNo,NAME,PRICE,DISCOUNT,DATE_AVAIL dateAvail,PHOTO,SALES_QTY salesQty,REQ_MSG reqMsg,DETAILS,INSTRUCTION,TYPE,SUB_COUNT subCount,STAR,REV_COUNT revCount,REGDATE			
FROM products
WHERE no=#{no}

SELECT NO,GIVER_NO giverNo,CATEGORY_NO categoryNo,NAME,PRICE,DISCOUNT,DATE_AVAIL dateAvail,PHOTO,SALES_QTY salesQty,REQ_MSG reqMsg,DETAILS,INSTRUCTION,TYPE,SUB_COUNT subCount,STAR,REV_COUNT revCount,REGDATE
FROM products 
WHERE giver_no = #{giverNo}

SELECT NO,GIVER_NO giverNo,CATEGORY_NO categoryNo,NAME,PRICE,DISCOUNT,DATE_AVAIL dateAvail,PHOTO,SALES_QTY salesQty,REQ_MSG reqMsg,DETAILS,INSTRUCTION,TYPE,SUB_COUNT subCount,STAR,REV_COUNT revCount,REGDATE
FROM products
WHERE no = #{productNo}

SELECT no, giver_no giverNo, name
FROM products
WHERE giver_no = #{giverNo}

SELECT no, giverNo, categoryNo, name, price, discount,realPrice, photo, salesQty, type, subCount, star,revCount, regdate,company, priorNo, categoryName, rownum r FROM(SELECT no, giverNo, categoryNo, name, price, discount,realPrice, photo, salesQty, type, subCount, star,revCount, regdate,company, priorNo, categoryName, rownum r FROM(SELECT DISTINCT p.no no, giver_no giverNo, p.name name, p.price price, p.discount discount, p.price-discount realPrice, p.photo photo, p.sales_qty salesQty, p.type type, p.sub_count subCount, p.star star, p.rev_count revCount, p.regdate regdate, g.company company, c.prior_no priorNo, c.no categoryNo, c.name categoryName FROM products p INNER JOIN givers g ON p.giver_no = g.no INNER JOIN categories c ON c.no = p.category_no INNER JOIN delivery_regions dr ON p.no = dr.product_no LEFT JOIN regions r ON r.no = dr.area_no
WHERE p.name LIKE '%'|| #{searchValue} || '%'
WHERE r.name LIKE '%'|| #{searchValue} || '%'
WHERE c.prior_no = #{categoryNo}
WHERE c.no = #{categoryNo}
AND r.name = #{location}
ORDER BY no
ORDER BY regdate
ORDER BY subCount
ORDER BY price ASC
ORDER BY price DESC
)) WHERE r BETWEEN #{start} AND #{end}

SELECT COUNT(DISTINCT p.no) FROM products p INNER JOIN categories c ON p.category_no = c.no INNER JOIN delivery_regions dr ON p.no = dr.product_no LEFT JOIN regions r ON r.no = dr.area_no
WHERE p.name LIKE '%'|| #{searchValue} || '%'
WHERE r.name LIKE '%'|| #{searchValue} || '%'
WHERE c.prior_no = #{categoryNo}
WHERE c.no = #{categoryNo}
AND r.name = #{location}

SELECT p.NO,GIVER_NO giverNo,CATEGORY_NO categoryNo,NAME,PRICE,DISCOUNT,DATE_AVAIL dateAvail,PHOTO,SALES_QTY salesQty,REQ_MSG reqMsg,DETAILS,INSTRUCTION,TYPE,SUB_COUNT subCount,STAR,REV_COUNT revCount,REGDATE, company
FROM products p JOIN givers g ON p.giver_no = g.no
WHERE p.no=#{no}

SELECT COUNT(*)
FROM products
WHERE giver_no = #{giverNo}
